import { 
  Button,
  StyleSheet,
  TextInput,
  View 
} from 'react-native';

export default function App() {

  return (
    <View style={{padding: 40}}>
      <View>
        <TextInput
          style={{borderBottomColor:"#CCC", borderBottomWidth:2, padding: 12, marginBottom: 4}}
          placeholder='Lembrar...'
        />
        <Button title='OK'/>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
